statements.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY RiQiShiKe DESC) AS 序号, convert(varchar(20),RiQiShiKe,20) as 日期时刻, PanHao as 盘号, PeiBiHao as 配比号, CheHao as 车号, KeHuHao as 客户号, round(GuLiao6, 2) as 骨料6, round(GuLiao5, 2) as 骨料5, round(GuLiao4, 2) as 骨料4, round(GuLiao3, 2) as 骨料3, round(GuLiao2, 2) as 骨料2, round(GuLiao1, 2) as 骨料1, round(ShiFen3, 2) as 石粉3, round(ShiFen2, 2) as 石粉2, round(ShiFen1, 2) as 石粉1, round(LiQing, 2) as 沥青, round(HeJi, 2) as 合计Kg, YiCangWd as 一仓温度, HunHeLiaoWd as 混合料温度, ChuChenQiWd as 除尘器入口温度, LiQingWd as 沥青温度, GuLiaoWd as 骨料温度 FROM dbData
WHERE RiQiShiKe BETWEEN '$StartTime$' AND '$EndTime$'
) AS T WHERE 序号 BETWEEN ($PageNumber$ - 1) * $PageSize$ + 1 AND $PageNumber$ * $PageSize$
